import React from "react";
import Bouton from "../../components/bouton/bouton";
class ViewOldMessages extends React.Component {

    state = { messages : [
        {
            auteur : "kevin",
            message : "blabla"
        },
        {
            auteur : "Julien Rey",
            message : "bloblo"
        },
        {
            auteur : "Maurice GABAY",
            message : "blublublu"
        }
    ]
    }  

    giveAllMessages = () => {
        var xhr = new XMLHttpRequest(); 
        xhr.open('GET', 'local');
        xhr.onreadystatechange = function() {
          if (xhr.readyState === 4) {
            var reponse = JSON.parse(xhr.responseText);
            alert(reponse.name);
          }
        };
        xhr.send();

    }

    render(){
        return(
            <> 
                {this.state.messages.map((messages, index) => {
                    return(
                        <div key={index}>
                            <p>{messages.auteur}</p>
                            <p> {messages.message}</p>

                        </div>
                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
