import React from "react";
import Entete from "../../../components/entete/entete";
import axios from "axios"
import { Redirect } from "react-router";
import {withFormik} from "formik"

class Inscription extends React.Component {
    state =      {  
            adresseMail : "",
            pseudonyme : "",
            password : "", 
            passwordConfirm : "", 
            biographie : "", 
            photo : "", 
            sexe : null

    }

    handleInscription = () => {

        const newUser = {
            adresseMail : this.state.adresseMail,
            pseudonyme : this.state.pseudonyme,
            password : this.state.password,
            textPresentation: this.state.biographie,
            photoPresentation: this.state.photo,
            sexe: this.state.sexe,
        }   
        // Vérifier que l'adresse mail ne soit pas déjà présente dans notre BDD 

        // Vérifier que le nom d'utilisateur n'existe pas déjà

        // Vérifier que les 2 mots de passes correspondent



        axios.post("http://localhost:8000/User/new", newUser)
        .then (res => {
            if(res.status === 200){
                /* this.props.history.replace("/") */
                window.location.replace("http://localhost:3000/")            }
        })
    } 

    render(){
        return(
            <>

            <div className="container">
                <form>
                    <fieldset>
                        <legend>INSCRIPTION - NOUVEL UTILISATEUR</legend>
                        <div className="form-group">
                            <label htmlFor="exampleInputEmail1" className="form-label mt-4">Adresse Email</label>
                            <input name="adresseMail" onChange={(event)=> this.setState({adresseMail:event.target.value})} type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="example@mail.com"/>
                            <small id="emailHelp" className="form-text text-muted">Nous ne partagerons pas votre adresse Mail.</small>
                        </div>
                        <div className="form-group">
                            <label htmlFor="exampleInputEmail1" className="form-label mt-4">Pseudonyme</label>
                            <input name="pseudonyme" onChange={(event)=> this.setState({pseudonyme:event.target.value})} type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="example@mail.com"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="exampleInputPassword1" className="form-label mt-4">Mot de passe</label>
                            <input name="password" onChange={(event)=> this.setState({password:event.target.value})} type="password" className="form-control" id="exampleInputPassword1" placeholder="azerTY%1230e"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="exampleInputPassword1" className="form-label mt-4">Confirmez votre mot de passe</label>
                            <input name="password2" onChange={(event)=> this.setState({passwordConfirm:event.target.value})} type="password" className="form-control" id="exampleInputPassword1" placeholder="azerTY%1230e"/>
                        </div>
                        <div className="form-group">
                        </div>
                        <div className="form-group">
                            <label  htmlFor="exampleTextarea" className="form-label mt-4" >Biographie</label>
                            <textarea  onChange={(event)=> this.setState({biographie:event.target.value})} className="form-control" id="exampleTextarea" rows="3" placeholder="Parlez nous un petit peu de vous"></textarea>
                        </div>
                        <div className="form-group">
                            <label htmlFor="formFile" className="form-label mt-4">Photo de profil</label>
                            <input onChange={(event)=> this.setState({photo:event.target.files[0].name})} className="form-control" type="file" id="formFile"/>
                        </div>
                        <fieldset className="form-group">
                            <legend className="mt-4">Sexe</legend>
                            <div className="form-check">
                                <label className="form-check-label">
                                <input onChange={(event)=> this.setState({sexe:event.target.value})} type="radio" className="form-check-input" name="optionsRadios" id="optionsRadios1" value="0"/>
                                Homme
                                </label>
                            </div>
                            <div className="form-check">
                                <label className="form-check-label">
                                <input onChange={(event)=> this.setState({sexe:event.target.value})} type="radio" className="form-check-input" name="optionsRadios" id="optionsRadios2" value="1"/>
                                Femme
                                </label>
                            </div>
                        </fieldset>
                        <button type="submit" className="btn btn-primary" onClick={() => this.handleInscription()}>Submit</button>
                    </fieldset>
                </form>
            </div>
        </>
        )
    }
}

export default withFormik({

    mapPropsToValues : () => ({
        adresseMail : "",
        pseudonyme : "",
        password : "", 
        passwordConfirm : "", 
        biographie : "", 
        photo : "", 
        sexe : null    
    }),
    validate : values => {
        // Contient les règles de validations
    },
    handleSubmit:(values, {props}) => {
        // Validation du formulaire en question
    }

    })(Inscription)
