import React, { Component } from "react";
import Bouton from "../../components/bouton/bouton";
class ViewOldMessages extends React.Component {

    state = {
        ilLoaded : false,
        messagess : []
}



componentDidMount(){
    fetch('http://localhost:3000/')
        .then(res => res.json())
        .then(res=> console.log(res))
        .then(function(res)
            {this.setState({messagess: res.data()}) }

        )
}    



    render(){
        console.log(this.state.messagess)

        return(
            <> 
                {this.state.messagess.map((message, index) => {
                    return(
                        <div key={index}>
                            <p>{message.auteur}</p>
                            <p> {message.message}</p>

                        </div>
                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
