import React from "react";
import {withFormik} from "formik"
import axios from "axios";

class UpdatePassword extends React.Component {


    handleUpdatePassword = () => {
        const password = this.props.values.password
        console.log(password)
        axios.post("http://localhost8000/updatePassword", password)
    }


    render(){
        return(
            <div className='container'>
            <fieldset>
                <legend>MODIFIER SON MOT DE PASSE</legend>
                <div className='container'>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword" className="form-label mt-4">Si vous souhaitez modifier votre mot de passe, inscrivez le ici</label>
                        <input onChange={this.props.handleChange} value={this.props.values.password} name="password"  type="password" className="form-control" id="password"  placeholder="Entrer un mot de passe"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword2" className="form-label mt-4">Confirmez votre nouveau mot de passe</label>
                        <input onChange={this.props.handleChange} value={this.props.values.password2}  name='password2' type="password" className="form-control" id="password2" placeholder="Confirmez le mot de passe"/>
                    </div>
                </div>
                <button onClick={this.handleUpdatePassword} type="submit" className="btn btn-primary">Valider</button>
            </fieldset>
        </div>
        )
    }
}

export default withFormik({

    mapPropsToValues : () => ({
        password : "", 
        password2 : ""
    }),
    validate : values => {
        // Contient les règles de validations
    },
    handleSubmit:(values, {props}) => {
        this.handleUpdatePassword()
    }

    })(UpdatePassword)
