import React, { Component } from "react";
import Bouton from "../../components/bouton/bouton";
import axios from 'axios'
class ViewOldMessages extends React.Component {

    state = {
        ilLoaded : false,
        messagess : []
    }



    componentDidMount = () => {
        axios.get("http://localhost:3000")
        .then(reponse => {
            console.log(re)
            const messagess = reponse.data.map(message => {
                return{
                    id: message.id,
                    auteur: message.auteur,
                    photo: message.photo,
                    message: message.message,
                    createdAt: message.createdAt,
                    updatedAt: message.updatedAt,
                    created : message.created
                }
        })
        this.setState({messagess})

    })    
        .catch(error => {
            console.log(error)
        })
    }


    render(){

        return(
            <> 
                {this.state.messagess.map((message, index) => {
                    return(
                        <div key={index}>
                            <p>{message.auteur}</p>
                            <p> {message.message}</p>
                        </div>
                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
