import axios from "axios";
import React from "react";
import Entete from "../../components/entete/entete";
import {withFormik} from "formik"
import { boolean } from "yup/lib/locale";
import Input from "../../components/bouton/input";

class PageProfil extends React.Component{

    state = {
        adresseMail : "", 
        pseudonyme : "", 
        textPresentation : "",
        photoPresentation : "",
        photo : "", 
        sexe : "",

        // Partie 2 , changement de photo
        fileSelected : "",
        fileSelectedName : ""
    }


    fileSelected = (event) => {
        // mettre l'image dans le state pour l'affichage de l'aperçu
       this.setState({fileSelected : URL.createObjectURL(event.target.files[0])})

       // mettre le nom de l'image dans le state pour le renvoyer au composant supérieur 
       const File = (event.target.files[0])
       this.setState({fileSelectedName : File})
    }

    componentDidMount = () =>{
        // Envoyer l'id 
        const send = "http://localhost:8000/getProfil/"
        const iDtoSend = localStorage.id
        // Récupérer les data de l'utilisateur et les ajouter dans les states
        axios.get(send+iDtoSend)
        .then(reponse => {
            console.log(reponse)
            // Récupérer les données de l'utilisateur pour alimenter les props.values
            this.props.values.adresseMail = reponse.data.user.adresseMail
            this.props.values.pseudonyme = reponse.data.user.pseudonyme
            this.props.values.textPresentation = reponse.data.user.textPresentation
            this.props.values.sexe = reponse.data.user.sexe
            this.props.values.createdAt = reponse.data.user.createdAt
            this.props.values.myphotoPresentation = reponse.data.user.photoPresentation
        // Alimenter les states pour les valeur par défault   
            const myuser = {
                adresseMail: this.props.values.adresseMail,
                pseudonyme: this.props.values.pseudonyme,
                textPresentation: this.props.values.textPresentation,
                photoPresentation: this.props.values.myphotoPresentation,
                sexe: reponse.data.user.sexe,
                createdAt: reponse.data.user.createdAt,
                updatedAt: Date.now() - (7 * 24 * 60 * 60 * 1000),
                created : reponse.data.user.created
                }            
            this.setState(myuser)
    })
    }

    handleChangePhoto = () => {
/*         const ImageName = Date.now() + this.state.fileSelectedName.name.split(" ").join("_")
        console.log("ImageName" , ImageName)
        const fd = new FormData()
        fd.append('image', this.state.fileSelected, ImageName)
        console.log('fd', fd)

        axios.post('http://localhost:8000/updatePhotoUser/41', fd)
        .then(res => {
         console.log(res)   
        }) */
        console.log(this.fileSelected)
    }


    render(){
        //Charger le sexe coché lors du clique 
        var hommeChecked = boolean
        var femmeChecked = boolean
        if(this.props.values.sexe){
             hommeChecked = true
             femmeChecked = false
        }
        if(!this.props.values.sexe){
            hommeChecked = false
            femmeChecked = true
        }
       /* récupérer (le chemin de) la photo du profil pour la balise image */
       const lienImage = `http://localhost:8000/images/${this.props.values.myphotoPresentation}`

        return(
            <>
                <Entete/>
                <div className="container">
                    <form>
                        <fieldset>
                            <legend>MODIFIER SES DONNEES DE PROFIL</legend>
                            {/* Sous bloc adresse mail */}
                            <div className="form-group row">
                                <label htmlFor="staticEmail" className="col-sm-2 col-form-label">Email</label>
                                <div className="col-sm-10">
                                    <input onChange={this.props.handleChange} value={this.props.values.adresseMail} name='adresseMail' type="text" readOnly="" className="form-control-plaintext" id="staticEmail" defaultValue={this.props.values.adresseMail}/>
                                </div>
                            </div>
                             {/* Sous bloc pseudonyme */}
                            <div className="form-group row">
                                <label htmlFor="staticEmail" className="col-sm-2 col-form-label">Pseudonyme</label>
                                <div className="col-sm-10">
                                    <input onChange={this.props.handleChange} value={this.props.values.pseudonyme} name="pseudonyme" type="text" readOnly="" className="form-control-plaintext" id="staticEmail" defaultValue={this.props.values.pseudonyme}/>
                                </div>
                            </div>
                            {/* Sous bloc Biographie */}
                            <div className="form-group">
                                <label htmlFor="exampleTextarea" className="form-label mt-4" defaultValue={this.state.textPresentation}>Décrivez vous en quelques mots</label>
                                <textarea onChange={this.props.handleChange} value={this.props.values.textPresentation} name='textPresentation' className="form-control" id="exampleTextarea" rows="3"></textarea>
                            </div>
                            {/* Sous bloc choix du sexe */}
                            <fieldset className="form-group">
                                <legend className="mt-4">Sexe</legend>
                                <div className="form-check">
                                    <label className="form-check-label">
                                    <input type="radio" className="form-check-input" name="optionsRadios" id="optionsRadios1" value="option1" defaultChecked={hommeChecked} />
                                    Homme
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label className="form-check-label">
                                    <input type="radio" className="form-check-input" name="optionsRadios" id="optionsRadios2" value="option2" defaultChecked={femmeChecked}/>
                                    Femme
                                    </label>
                                </div>
                            </fieldset>
                            <button type="button"  className="btn btn-primary">Valider</button>
                        </fieldset>
                    </form>
                </div>
                {/* Bloc choix de la photo de profil */}
                <div className='container'>
                    <legend>MODIFIER SA PHOTO DE PROFIL</legend>
                        <img src={lienImage} alt="" />
                        {console.log(lienImage)}
                    <fieldset>
                        <input onChange={event => this.fileSelected(event)}value={this.props.values.photoPresentation} defaultValue={this.props.values.photoPresentation}  name='photo' className="form-control" type="file" id="formFile"/>
                    </fieldset>
                    <button onClick={() => this.handleChangePhoto()}  type="submit" className="btn btn-primary">Valider</button>
                </div>

                <div className='container'>
                    <fieldset>
                        <legend>MODIFIER SON MOT DE PASSE</legend>
                        <div className='container'>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword" className="form-label mt-4">Si vous souhaitez modifier votre mot de passe, inscrivez le ici</label>
                                <input  name="password"  type="password" className="form-control" id="password"  placeholder="Entrer un mot de passe"/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputPassword2" className="form-label mt-4">Confirmez votre nouveau mot de passe</label>
                                <input name='password2' type="password" className="form-control" id="password2" placeholder="Confirmez le mot de passe"/>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary">Valider</button>
                    </fieldset>
                </div>
            </>
        )
    }
}

export default withFormik({

    mapPropsToValues : () => ({
        adresseMail : "", 
        pseudonyme : "", 
        textPresentation : "",
        photo : "", 
        sexe : ""    
    }),
    validate : values => {
        // Contient les règles de validations
    },
    handleSubmit:(values, {props}) => {
        // Validation du formulaire en question
    }

    })(PageProfil)
