import React from "react";
import Bouton from "../../components/bouton/bouton";
import Input from "../../components/bouton/input";
import classes from "./modifyMessage.module.css"
import axios from "axios";
import Entete from "../entete/entete";

class ModifyMessage extends React.Component {

    state = {

        messagetoModify : [

            {id : 72},
            {auteur: ""},
            {photo: ""},
            {message: "qsd"},
            {createdAt: ""},
            {updatedAt: ""},
            {created : ""}  
        ]
    }




        // Fonction de liaison avec la BDD
           componentDidMount = () => {

            // Appeler tous les messages dans la BDD et les rajouter dans les states
            const nouveauState = this.state.messagetoModify.slice()
            const IdtoModify = this.state.messagetoModify[0].id
             axios.get(`http://localhost:8000/byID/${IdtoModify}`)
            .then(reponse => {

                // Rajouter les données récupérées dans les states
                const newStates = [...this.state.messagetoModify]
                console.log(newStates)
                newStates[id] = (reponse.data.data.auteur)
                newStates[auteur] = (reponse.data.data.id)
                newStates[photo] = (reponse.data.data.photo)
                newStates[message] = (reponse.data.data.message)
                newStates[createdAt] = (reponse.data.data.createdAt)
                newStates[updatedAt] = Date.now() - (7 * 24 * 60 * 60 * 1000)

                this.setState({messagetoModify : newStates})
            })


            .catch(error => {
                console.log(error)
            })     
    }


            render(){
                const classBloc = `${classes.modifyMessage} container w-100 `
                return (
                    <>
                    <Entete/>
                    <form className={classBloc}>
                        <div className='row'>
                        <label htmlFor="exampleTextarea" className="form-label mt-4">Ecrivez votre message et cliquez sur postez pour qu'il se rajoute aux posts du forum</label>
                            <div className="form-group col-8">
                                <textarea 
                                className="form-control" 
                                id="exampleTextarea" rows="3" 
                                value={this.state.messagetoModify.message}
                                onChange={(event) => this.setState({message:event.target.value})}
                                />
                            </div>
                            <div className="form-group col-4">
                                <Input> Parcourir</Input>
                                <Bouton couleur="warning" type="button" clic={() => this.props.confirmerModification()}> Modifier </Bouton>
                            </div>
                        </div>
                    </form>
                    </>
                )
            }
}

export default ModifyMessage
