import React from "react"
import axios from "axios"

class CadreUtilisateur extends React.Component {
    state = {
        userPhoto : "", 
        userName : ""

    }

    componentDidMount = () => {
        axios.get(`http://localhost:8000/getProfil/${this.props.id}`)// Modifier l'id avec la valeur des props
        .then (res => {
             this.setState({userPhoto : res.data.user.photoPresentation})
             this.setState({userName : res.data.user.pseudonyme})

})
    }

    render(){
        return(
            <div className="cadreUser">
                <h1>{this.state.userName}</h1>
                <img src={`http://localhost/images/${this.state.userPhoto}`}  />
            </div>

        )
    }
}

export default CadreUtilisateur
