import React from "react";
import Entete from "./entete/entete";
import PostMessages from "./postMessage/postMessage";
import axios from 'axios'

class PageMessage extends React.Component {

    state = {
        ilLoaded : false,
        messagess : [],

        messageSaisi : "",
        imageSaisi : "none",
        auteurSaisi : "default",
        createdAtSaisi : "",
        updatedAtSaisi : "",

    }

    componentDidMount = () => {
        axios.get("http://localhost:8000/")
        .then(reponse => {
            const messagess = reponse.data.data.map(message => {
                return{
                    id: message.id,
                    auteur: message.auteur,
                    photo: message.photo,
                    message: message.message,
                    createdAt: message.createdAt,
                    updatedAt: message.updatedAt,
                    created : message.created
                }
        })
        this.setState({messagess})

    })    
        .catch(error => {
            console.log(error)
        })
    }


    // Mettre à jour l'heure dans les states
    componentDidMount() {
        setInterval( () => {
          this.setState({
            createdAtSaisi : new Date().toLocaleString(),
            updatedAtSaisi : new Date().toLocaleString()
          })
        },1000)
      }
    // Poster un nouveau message 
    postMessages = () => {
        axios.post('http://localhost:8000/createMessage/', {
                message : this.state.messageSaisi,
                image : this.state.imageSaisi,
                auteur : this.state.auteurSaisi,
                createdAt : this.state.createdAtSaisi,
                updatedAt : this.state.updatedAtSaisi,
                created : this.state.createdAtSaisi
            }
        )
        .then(function (reponse) {
            //On traite la suite une fois la réponse obtenue 
            console.log(reponse);
        })
        .catch(function (erreur) {
            //On traite ici les erreurs éventuellement survenues
            console.log(erreur);
        }); 
    }

    HandleDeleteMessage = (id) => {
        const messageIndex = this.state.messagess.findIndex(l => {
            return l.id === id
        })

        // Supprime dans les states
        const newmessagess = [...this.state.messagess]
        newmessagess.splice(messageIndex, 1)

        this.setState({messagess : newmessagess})
        console.log(newmessagess)
        //Supprime dans la base de donnée
        const pathWithID = `http://localhost:8000/delete`
        console.log(pathWithID, "l'id supprimé :", id)

        axios.delete(pathWithID, {
             data: { id: id },
             headers: { "Authorization": "clé d'autorisation" }
        });
    }


    render(){

        return(
            <>
                <Entete />
                <PostMessages 
                    sumbit={this.postMessages}
                    value={this.state.messageSaisi}

                />
                {/* Affichage de tous les messages postés ! */} 
                {this.state.messagess.map((message, index) => {
                    return(
                        <div id={this.props.id} className="container ">
                        <div className="row pull-left">
                                <div className=" card border-success mb-3 w-50 col-10"  >
                                    <div className="card-header">
                                        {message.createdAt}
                                    </div>
                                    <div className="card-body">
                                        <h4 className="card-title">{message.auteur}</h4>
                                        <p className="card-text">{message.message}</p>
                                    </div>
                                </div>
                                {/* Bloc menu déroulant des Actions : Modifier et Supprimer */}
                                <div className="btn-group col-1 h-25 " role="group" aria-label="Button group with nested dropdown">
                                <button type="button" className="btn btn-dark">Actions</button>
                                <div className="btn-group" role="group">
                                    <button id="btnGroupDrop4" type="button" className="btn btn-dark dropdown-toggle" value={message.id} data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false"></button>
                                    <div className="dropdown-menu" aria-labelledby="btnGroupDrop4" >
                                    <a className="dropdown-item" href="www.google.fr">Modifier le message</a>
                                    <a className="dropdown-item" onClick={message.deleteMessage} href="localhost:8000/delete/2" >Supprimer le message</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    )
                })}

            </>
            )
    }
}

export default PageMessage
