import React from "react";
import classes from "./connexion.module.css"
import { Link } from 'react-router-dom'
import {withFormik} from "formik"

class Connexion extends React.Component {

    state = {
        adresseMail : "",
        password : ""
    }

    render(){

        const data = {
             "adresseMail" : this.state.adresseMail,
             "password" : this.state.password
        }
        return (
            <form>
                <div className="container col-lg-6 col-md-8 col-sm-10 col-xs-12">
                    <h3>CONNECTEZ VOUS</h3>
                    <div className="form-group">
                        <label>Adresse Email</label>
                        <input  type="email" className="form-control" placeholder="Enter email" 
                                name="adressMail"
                                value={this.props.values.adresseMail}
                                onChange={this.props.handleChange} 
                                />
                    </div>

                    <div className="form-group">
                        <label>Mot de Passe</label>
                        <input  onChange={(event) => this.setState({password:event.target.value})} type="password" className="form-control" placeholder="Enter password" 
                                name="password"
                                value={this.props.values.password}
                                onChange={this.props.handleChange} 
                        />
                    </div>

                    <div className="form-group">
                        <div className="custom-control custom-checkbox">
                            <input type="checkbox" className="custom-control-input" id="customCheck1" />
                            <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                        </div>
                    </div>

                    <button className="btn btn-dark btn-lg btn-block" type='button' onClick={()  => this.props.Connect((data))}>Connexion</button>
                    <p className="forgot-password text-right">
                        Forgot <a href="#">password?</a>
                    </p>
                    <h3> PAS ENCORE INSCRIT, CLIQUEZ ICI</h3>

                    <button type="submit" className="btn btn-dark btn-lg btn-block" >
                        <Link 
                            to={{
                                pathname : "/Inscription"
                                }} 
                            className="dropdown-item" 
                            >
                            Inscription
                        </Link> 
                    </button>
                </div>
            </form>
        )
    }
}
