import axios from "axios"
import {useParams, Link} from "react-router-dom"




class ModifyMessage extends React.Component {

    state = {
        messagetoModify : [
            {
            auteur: "",
            photo: "",
            message: "qsd",
            createdAt: "",
            updatedAt: "",
            created : ""
        }
    ]
    }












/* Récupérer les infos de l'id en SLUG */
    componentDidMount = () => {


        // Appeler tous les messages dans la BDD 
        axios.get(`http://localhost:8000/byID/72`)
        .then(reponse => {
            console.log(reponse)
        })

        .catch(error => {
            console.log(error)
        })
    }

        // fonction permettant d'obtenir un paramètre url
    $_GET = (param) =>  {
        var vars = {};
        window.location.href.replace(window.location.hash, '').replace(
            /[?&]+([^=&]+)=?([^&]*)?/gi, // regexp
            function (m, key, value) { // callback
                vars[key] = value !== undefined ? value : '';
            }
        );

        if (param) {
            return vars[param] ? vars[param] : null;
        }
        return vars;
    }







            render(){
                const classBloc = `${classes.modifyMessage} container w-100 `
                const myID = this.$_GET('data-id')
                return (
                    <>
                    <Entete/>
                    <form className={classBloc}>
                        <div className='row'>
                        <label htmlFor="exampleTextarea" className="form-label mt-4">Ecrivez votre message et cliquez sur postez pour qu'il se rajoute aux posts du forum</label>
                            <div className="form-group col-8">
                                <textarea 
                                className="form-control" 
                                id="exampleTextarea" rows="3" 
                                value={this.state.messagetoModify.message}
                                onChange={(event) => this.setState({message:event.target.value})}
                                />
                            </div>
                            <div className="form-group col-4">
                                <Input> Parcourir</Input>
                                <Bouton couleur="warning" type="button" clic={() => this.props.confirmerModification()}> Modifier </Bouton>
                            </div>
                        </div>
                    </form>
                    </> 
                )
            }
        }

export default ModifyMessage
