import React from "react";
import axios from 'axios'
class ViewOldMessages extends React.Component {

    state = {
        ilLoaded : false,
        messagess : []
    }



    componentDidMount = () => {
        axios.get("http://localhost:8000/")
        .then(reponse => {
            console.log(reponse.data.data)
            const messagess = reponse.data.data.map(message => {
                return{
                    id: message.id,
                    auteur: message.auteur,
                    photo: message.photo,
                    message: message.message,
                    createdAt: message.createdAt,
                    updatedAt: message.updatedAt,
                    created : message.created
                }
        })
        this.setState({messagess})

    })    
        .catch(error => {
            console.log(error)
        })
    }


    render(){

        return(
            <> 
                {this.state.messagess.map((message, index) => {
                    return(
                            <div key={index}>
                                    <p></p>
                                    <p> {message.message}</p>                
                                    <div className="card text-white bg-danger mb-3" >
                                    <div className="card-header">{message.created}</div>
                                    <div className="card-body">
                                        <h4 className="card-title">{message.created}</h4>
                                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                    </div>
                                    </div>
                            </div>

                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
