import React from "react";
import Entete from "../../../components/entete/entete";
import axios from "axios"
import { Redirect } from "react-router";
import {withFormik} from "formik"
import * as Yup from "yup"

class Inscription extends React.Component {

    state = {
        fileSelected : "",
        fileSelectedName : ""
    }  

    fileSelected = (event) => {
        // mettre l'image dans le state pour l'affichage de l'aperçu
       this.setState({fileSelected : URL.createObjectURL(event.target.files[0])})

       // mettre le nom de l'image dans le state pour le renvoyer au composant supérieur 
       const File = (event.target.files[0])
       this.setState({fileSelectedName : File})
    }

    handleInscription = () => {
        // enregistrement du profil sans photo 
        if(!this.state.fileSelected){
            const newUser = {
                adresseMail : this.state.adresseMail,
                pseudonyme : this.state.pseudonyme,
                password : this.state.password,
                textPresentation: this.state.biographie,
                photoPresentation: "photoStandard.jpg",
                sexe: this.state.sexe,
            }   
            // Vérifier que l'adresse mail ne soit pas déjà présente dans notre BDD 

            // Vérifier que le nom d'utilisateur n'existe pas déjà

            // Vérifier que les 2 mots de passes correspondent
            axios.post("http://localhost:8000/User/new", newUser)
            .then (res => {
                if(res.status === 200){
                    /* this.props.history.replace("/") */
                    window.location.replace("http://localhost:3000/")            
                }
            })
        }
        // Enregistrement du profil avec sa Photo
        const ImageName = Date.now() + this.state.fileSelectedName.name.split(" ").join("_")
        console.log("ImageName" , ImageName)
        const fd = new FormData()
        fd.append('image', this.state.fileSelectedName, ImageName)
        console.log('fd', fd)

        const path = "http://localhost:8000/uploadPhoto/"
        axios.post("http://localhost:8000/upload/", fd , {headers: {"token": localStorage.getItem("token"), id: localStorage.getItem("id")}})
