import React from "react";
import classes from "./connexion.module.css"
import { Link } from 'react-router-dom'
import {withFormik} from "formik"
import * as Yup from "yup"


class Connexion extends React.Component {

    state = {
        adresseMail : "",
        password : ""
    }

    render(){

        const data = {
             "adresseMail" : this.state.adresseMail,
             "password" : this.state.password
        }
        return (
            <form>
                <div className="container col-lg-6 col-md-8 col-sm-10 col-xs-12">
                    <h3>CONNECTEZ VOUS</h3>
                    <div className="form-group">
                        <label>Adresse Email</label>
                        <input value={this.props.values.adresseMail} name="adresseMail" onChange={this.props.handleChange} type="string" className="form-control" placeholder="Enter email" />
                        {this.props.errors.adresseMail && <span style={{color:"red"}}> {this.props.errors.adresseMail} </span>}
                    </div>

                    <div className="form-group">
                        <label>Mot de Passe</label>
                        <input value={this.props.values.password} name="password" onChange={this.props.handleChange}  type="string" className="form-control" placeholder="Enter password" />
                    </div>

                    <div className="form-group">
                        <div className="custom-control custom-checkbox">
                            <input type="checkbox" className="custom-control-input" id="customCheck1" />
                            <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                        </div>
                    </div>

                    <button className="btn btn-dark btn-lg btn-block" onClick={this.props.handleSubmit}>Connexion</button>
                    <p className="forgot-password text-right">
                        Forgot <a href="#">password?</a>
                    </p>
                    <h3> PAS ENCORE INSCRIT, CLIQUEZ ICI</h3>

                    <button type="submit" className="btn btn-dark btn-lg btn-block" >
                        <Link 
                            to={{
                                pathname : "/Inscription"
                                }} 
                            className="dropdown-item" 
                            >
                            Inscription
                        </Link> 
                    </button>
                </div>
            </form>
        )
    }
}
export default withFormik({

    mapPropsToValues : () => ({
        adresseMail : "",
        password : ""
    }),
    validationSchema : Yup.object().shape({
        adresseMail : Yup.string()
        .required("le champ adresse Mail est obligatoire pour se connecter")

    }),
    handleSubmit : (values, {props}) => {
        const data = {
            "adresseMail" : values.adresseMail,
            "password" : values.password
        }
        props.Connect((data))
    }

    })(Connexion)
