import React from "react";
import {withFormik} from "formik"
import axios from "axios";
import * as Yup from "yup"


class UpdatePassword extends React.Component {


    handleUpdatePassword = () => {
        const id = localStorage.id
        const password = {password : this.props.values.password}
        console.log(password)
        axios.post(`http://localhost:8000/updatePassword/${id}`, password)
     }


    render(){
        return(
            <div className='container'>
                <div className=" mt-4 text-center text-success">
                    <h2> MODIFIER SON MOT DE PASSE</h2>
                </div>  
                <div className='container'>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword" className="form-label mt-4">Si vous souhaitez modifier votre mot de passe, inscrivez le ici</label>
                        <input onChange={this.props.handleChange} value={this.props.values.password} name="password"  type="password" className="form-control" id="password"  placeholder="Entrer un mot de passe"/>
                        {this.props.errors.password  && this.props.errors.password && <span style={{color:"red"}}> {this.props.errors.password}</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword2" className="form-label mt-4">Confirmez votre nouveau mot de passe</label>
                        <input onChange={this.props.handleChange} value={this.props.values.password2}  name='password2' type="password" className="form-control" id="password2" placeholder="Confirmez le mot de passe"/>
                        {this.props.errors.password2  && this.props.errors.password2 && <span style={{color:"red"}}> {this.props.errors.password2}</span>}
                    </div>
                </div>
                <button onClick={this.handleUpdatePassword} type="button" className="btn btn-primary">Valider</button>
        </div>
        )
    }
}

export default withFormik({

    mapPropsToValues : () => ({
        password : "", 
        password2 : ""
    }),
    validationSchema : Yup.object().shape({
        password : Yup.string()
        .min(3, "Pas assez de caractères")
        .max(16, "Trop de caractères"),
        password2: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Les 2 mots de passe ne correspondent pas')
    }),
    handleSubmit:(values, {props}) => {
        this.handleUpdatePassword()
    }

    })(UpdatePassword)
