import React from "react";
import Bouton from "../../components/bouton/bouton";
import Input from "../../components/bouton/input";
import classes from "./modifyMessage.module.css"
import Entete from "../../components/entete/entete";
import axios from "axios"
import { Link} from "react-router-dom"




class ModifyMessage extends React.Component {
    state = {
        messagetoModify : [
            {
            id: "",
            auteur: "",
            photo: "",
            message: "",
            createdAt: "",
            updatedAt: "",
            created : ""
        }
    ],

    }

/* Récupérer les éléments du message à modifier en fonction de son id */
    componentDidMount = () => {
        let ID = this.props.match.params.id
        // Appeler tous les messages dans la BDD 
        axios.get(`http://localhost:8000/byID/${ID}`)
        .then(reponse => {
            // On récupère l'ancien state vierge
            const NewState = [...this.state.messagetoModify]
            // J'ajoute toutes les données, l'ID récupéré du paramètre d'URL, le reste du serveur
            NewState.id = ID
            NewState.auteur = reponse.data.data.auteur
            NewState.photo = reponse.data.data.photo
            NewState.message = reponse.data.data.message
            NewState.createdAt = reponse.data.data.createdAt
            NewState.updatedAt = reponse.data.data.updatedAt
            // Je mets à jour mes states avec les données que j'ai récupérées du serveur
            this.setState({messagetoModify : NewState})
        })
        .catch(error => {
            console.log(error)
        })
    }


    confirmerModification = () => {
        // préparation de notre envoi au serveur Node
        const messageModifie = {
            message : this.state.messagetoModify.message,
            auteur : this.state.messagetoModify.auteur,
            photo : this.state.messagetoModify.photo,
        }
        let ID = this.props.match.params.id
        console.log(ID)
        // Envoi de notre modification à Node
        axios.post(`http://localhost:8000/modifyMessage/${ID}`,
            messageModifie
        )
        .then(function(reponse) {
            console.log('modification envoyée au serveur')
        })    
        .then(error => {
            console.log("front : il y a eu cette erreure", error)
        })
        // Remettre à jour la liste des Messages sur la BDD
        axios.get("http://localhost:8000/")
        .then(reponse => {
            const messagess = reponse.data.data.map(message => {
                return{
                    id: message.id,
                    auteur: message.auteur,
                    photo: message.photo,
                    message: message.message,
                    createdAt: message.createdAt,
                    updatedAt: message.updatedAt,
                    created : message.created
                }
        })
        this.setState({messagess})
        })
        this.setState({})
    }    
            render(){
                const classBloc = `${classes.modifyMessage} container w-100 `

                console.log(this.state)

                return (
                    <>
                    <Entete/>
                    <form className={classBloc}>
                        <div className='row'>
                        <label htmlFor="exampleTextarea" className="form-label mt-4">Ecrivez votre message et cliquez sur postez pour qu'il se rajoute aux posts du forum</label>
                            <div className="form-group col-8">
                                <textarea 
                                className="form-control" 
                                id="exampleTextarea" rows="3" 
                                value={this.state.messagetoModify.message}
                                onChange={(event) => {
                                    const newStates = [...this.state.messagetoModify]
                                    newStates.message = event.target.value
                                    this.setState({messagetoModify : newStates})
                                }}
                                />
                            </div>
                            <div className="form-group col-4">
                                <Input> Parcourir</Input>

                                <Bouton className="" couleur="secondary" type="button" clic={() => this.confirmerModification()}> 
                                    <Link  to="/accueil/"> MODIFIER </Link>
                                 </Bouton>

                                <Bouton className="color-white" couleur="secondary" type="button"  > 
                                    <Link to="/accueil/"> Retour </Link>
                                 </Bouton>
                            </div>
                        </div>
                    </form>
                    </> 
                )
            }
        }

export default ModifyMessage
