import React from "react";
import classes from "./postMessage.module.css"
import Input from "../../components/bouton/input";
import Bouton from "../../components/bouton/bouton";
import axios from "axios"

class PostMessages extends React.Component {

    state = {
        messageSaisi : "",
        imageSaisi : "none",
        auteurSaisi : "default",
        createdAtSaisi : "",
        updatedAtSaisi : "",

    }

    // Mettre à jour l'heure dans les states
    componentDidMount() {
        setInterval( () => {
          this.setState({
            createdAtSaisi : new Date().toLocaleString(),
            updatedAtSaisi : new Date().toLocaleString()
          })
        },1000)
      }
    // Poster un nouveau message 
    postMessages = () => {
        axios.post('http://localhost:8000/createMessage/', {
                message : this.state.messageSaisi,
                image : this.state.imageSaisi,
                auteur : this.state.auteurSaisi,
                createdAt : this.state.createdAtSaisi,
                updatedAt : this.state.updatedSaisi,
                created : this.state.createdAtSaisi
            }
        )
        .then(function (reponse) {
            //On traite la suite une fois la réponse obtenue 
            console.log(reponse);
        })
        .catch(function (erreur) {
            //On traite ici les erreurs éventuellement survenues
            console.log(erreur);
        });




    }

    render(){

        const classBloc = `${classes.blocFormulaire} container w-100 `

        return (
            <form className={classBloc}>
                <div className='row'>
                <label htmlFor="exampleTextarea" className="form-label mt-4">Ecrivez votre message et cliquez sur postez pour qu'il se rajoute aux posts du forum</label>
                    <div className="form-group col-8">
                        <textarea 
                        className="form-control" 
                        id="exampleTextarea" rows="3" 
                        value={this.state.post}
                        onChange={(event) => this.setState({messageSaisi:event.target.value})}
                        />
                    </div>
                    <div className="form-group col-4">
                        <Input> Parcourir</Input>
                        <Bouton couleur="secondary" type="button" submitMessage={this.postMessages}> Envoyer </Bouton>
                    </div>
                </div>

            </form>
        )
    }
}

export default PostMessages
