import React from "react";
import classes from "./postMessage.module.css"
import Input from "../../components/bouton/input";
import Bouton from "../../components/bouton/bouton";

class PostMessages extends React.Component {

    state = {
        post : "",
        image : "none",
        auteur : "default",
        createdAt : "2021-09-08T16:38:20.000Z",
        updatedAt : "2021-09-08T16:38:20.000Z"
    }

    returnMessages = () => {
        componentDidMount = () => {
            axios.get("http://localhost:8000/")
            .then(reponse => {
                const messagess = reponse.data.data.map(message => {
                    return{
                        id: message.id,
                        auteur: message.auteur,
                        photo: message.photo,
                        message: message.message,
                        createdAt: message.createdAt,
                        updatedAt: message.updatedAt,
                        created : message.created
                    }
            })
            this.setState({messagess})

        })    
            .catch(error => {
                console.log(error)
            })
        }        
    }

    render(){

        const classBloc = `${classes.blocFormulaire} container w-100 `

        return (
            <form className={classBloc}>
                <div className='row'>
                <label htmlFor="exampleTextarea" className="form-label mt-4">Ecrivez votre message et cliquez sur postez pour qu'il se rajoute aux posts du forum</label>
                    <div className="form-group col-8">
                        <textarea 
                        className="form-control" 
                        id="exampleTextarea" rows="3" 
                        value={this.state.post}
                        onChange={(event) => this.setState({post:event.target.value})}
                        />
                    </div>
                    <div className="form-group col-4">
                        <Input> Parcourir</Input>
                        <Bouton couleur="secondary" type="submit" submitMessage={this.returnMessages}> Envoyer </Bouton>
                    </div>
                </div>

            </form>
        )
    }
}

export default PostMessages
