import React from "react"
import axios from "axios"
import classes from "./cadreUtilisateur.css"

class CadreUtilisateur extends React.Component {
    state = {
        userPhoto : "", 
        userName : "",
        sexe : ""

    }

    componentDidMount = () => {
        axios.get(`http://localhost:8000/getProfil/${this.props.id}`)// Modifier l'id avec la valeur des props
        .then (res => {
             this.setState({userPhoto : res.data.user.photoPresentation})
             this.setState({userName : res.data.user.pseudonyme})
             this.setState({sexe : res.data.user.sexe})

})
    }

    render(){
        return(
            <div className={this.state.sexe === "Homme" ? "cadreUser card border-info mb-3" : "cadreUser card border-danger mb-3"}>
                <p className="text-center .text-danger">{this.state.userName}</p>
                <img className="imgPerso" src={`http://localhost:8000/images/${this.state.userPhoto}`}  />
            </div>

        )
    }
}

export default CadreUtilisateur
