import Bouton from "../../components/bouton/bouton";
class ViewOldMessages extends React.Component {

    state = {
        messages : [
            {
                auteur : "kevin",
                message : "blabla"
            },
            {
                auteur : "Julien Rey",
                message : "bloblo"
            },
            {
                auteur : "Maurice GABAY",
                message : "blublublu"
            }
        ],
        data : []
}



componentDidMount(){
    fetch('http://localhost:3000/').then(
        function(response){
            return response.json();
        }
        .then(function(res){
            this.setState({data: res.json()})
        })
        )
}





    render(){
        console.log(this.state.data)

        return(
            <> 
                {this.state.messages.map((messages, index) => {
                    return(
                        <div key={index}>
                            <p>{messages.auteur}</p>
                            <p> {messages.message}</p>

                        </div>
                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
