import React from "react";
import axios from 'axios'
import OneMessage from "./message/oneMessage";

class ViewOldMessages extends React.Component {

    state = {
        ilLoaded : false,
        messagess : []
    }



    componentDidMount = () => {
        axios.get("http://localhost:8000/")
        .then(reponse => {
            console.log(reponse.data.data)
            const messagess = reponse.data.data.map(message => {
                return{
                    id: message.id,
                    auteur: message.auteur,
                    photo: message.photo,
                    message: message.message,
                    createdAt: message.createdAt,
                    updatedAt: message.updatedAt,
                    created : message.created
                }
        })
        this.setState({messagess})

    })    
        .catch(error => {
            console.log(error)
        })
    }


    render(){

        return(
            <> 
                {this.state.messagess.map((message, index) => {
                    return(
                            <>
                            <ViewOldMessages
                                id = {this.message.id}
                                auteur = {this.message.auteur}
                                photo = {this.message.photo}
                                auteur = {this.message.auteur}
                                auteur = {this.message.auteur}
                                auteur = {this.message.auteur}

                            />


                            <div className="container" key={index}>
                                    <div className="card border-success mb-3" >
                                    <div className="card-header">{message.created}</div>
                                    <div className="card-body">
                                        <h4 className="card-title">{message.auteur}</h4>
                                        <p className="card-text">{message.message}</p>
                                    </div>
                                    </div>
                            </div>

                            </>



                    )
                })}


            </>
            )
    }
}
export default ViewOldMessages
